<template>
  <button
    :class="buttonClass"
    @click="buttonClick"
    @mouseover="buttonHover"
    @mouseout="buttonLeave"
  >
    <span :class="iconClass"></span>
  </button>
</template>

<script>
export default {
  name: 'CircleButton',
  props: {
    color: {
      type: String,
      default: 'white',
    },
    icon: {
      type: String,
      default: 'add',
    },
  },

  computed: {
    buttonClass() {
      return {
        'circled-button': true,
        [`circled-button--${this.color}`]: true,
        [`circled-button--${this.icon}`]: true,
      };
    },

    iconClass() {
      return {
        'circled-button__icon': true,
        [`circled-button__icon--${this.icon}`]: true,
      };
    },
  },

  methods: {
    buttonClick(event) {
      this.$emit('click', event);
    },

    buttonHover(event) {
      this.$emit('mouseenter', event);
    },

    buttonLeave(event) {
      this.$emit('mouseout', event);
    },
  },
};
</script>

<style lang="scss">
  .circled-button {
    display: flex;
    line-height: 1;
    padding: 8px;
    width: 48px;
    height: 48px;
    min-width: 48px;
    min-height: 48px;
    border-radius: 50%;
    border: 0;
    box-shadow: 2px 2px 8px 0 rgba(0,0,0,.4);
    pointer-events: all;
    cursor: pointer;
    transition: all .5s;

    &:hover,
    &:focus {
      outline: none;
    }

    &--white {
      background: #fff;
    }

    &--green {
      background: #4CAF50;
    }

    &--blue {
      background: #2196F3;
    }

    &--red {
      background: #e57373;
    }

    &--violet {
      background: #5E35B1;
    }

    &--grey {
      background: rgba(84,110,122 ,1);
    }

    &__icon {
      width: 32px;
      height: 32px;
      transition: all .5s;
      pointer-events: none;

      &--add {
        background: url(data:image/svg+xml;utf8;base64,);
      }

      &--branch {
        background: url('data:image/png;base64,');
      }

      &--gear {
        background: url(data:image/svg+xml;utf8;base64,);
      }

      &--pen {
        background: url(data:image/svg+xml;utf8;base64,);
      }

      &--table {
        background: url(data:image/svg+xml;utf8;base64,);
      }
    }
  }
</style>
